            Thread.sleep(DELAY)
            DataConverter.map(gson.fromJson(NOW_PLAYING_MOVIES, ApiMovies::class.java))
        }

    fun getUpcomingMovies(): Single<List<ShortMovie>> =
        Single.fromCallable {
            Thread.sleep(DELAY)
            DataConverter.map(gson.fromJson(UPCOMING_MOVIES, ApiMovies::class.java))
        }

    fun getPopularMovies(): Single<List<ShortMovie>> =
        Single.fromCallable {
            Thread.sleep(DELAY)
            DataConverter.map(gson.fromJson(POPULAR_MOVIES, ApiMovies::class.java))
        }

    fun getMovie(id: Int): Single<FullMovie> =
        Single.fromCallable {
            Thread.sleep(DELAY)
            DataConverter.map(gson.fromJson(MOVIE, ApiFullMovie::class.java))
        }

